import { createStore } from "vuex";

const store = createStore({
  state() {
    return {
      count: 1000,
      transaction: null,
    };
  },
  mutations: {
    increment(state) {
      state.count++;
    },
    setTransaction(state, transactionPayload) {
      state.transaction = transactionPayload;
    },
  },
